import React, { Component } from "react";
import { QUESTIONS } from "./questions";
import {storage} from 'node-persist'

class App extends Component {
  state = {
    currentQuestion: 1,
    answers: {},
    averageScore: 0,
    totalRuns: 0,
  };

  async componentDidMount() {
    await storage.init(); // Initialize Node-Persist
    await this.calculateAverageScore(); // Calculate average score on component mount
  }

  async calculateAverageScore() {
    const totalRuns = await storage.getItem("totalRuns") || 0;
    const totalScore = await storage.getItem("totalScore") || 0;
    const averageScore = totalRuns === 0 ? 0 : totalScore / totalRuns;
    this.setState({ averageScore, totalRuns });
  }

  handleAnswer = (answer) => {
    const { currentQuestion, answers } = this.state;
    const updatedAnswers = { ...answers, [currentQuestion]: answer };
    this.setState({ answers: updatedAnswers });
  };

  handleNextQuestion = () => {
    this.setState((prevState) => ({
      currentQuestion: prevState.currentQuestion + 1,
    }));
  };

  calculateScore = () => {
    const { answers } = this.state;
    const totalQuestions = Object.keys(QUESTIONS).length;
    const yesCount = Object.values(answers).filter((answer) => answer === "Yes").length;
    return yesCount / totalQuestions;
  };

  async handleSubmit() {
    const score = this.calculateScore();
    const { totalRuns } = this.state;
    const totalScore = await storage.getItem("totalScore") || 0;
    await storage.setItem("totalScore", totalScore + score);
    await storage.setItem("totalRuns", totalRuns + 1);
    await this.calculateAverageScore();
  }

  render() {
    const { currentQuestion, averageScore } = this.state;
    const totalQuestions = Object.keys(QUESTIONS).length;
    const isLastQuestion = currentQuestion === totalQuestions;

    return (
      <div className="main__wrap">
        <main className="container">
          <div>
            {currentQuestion <= totalQuestions ? (
              <div>
                <h1>Question {currentQuestion}</h1>
                <p>{QUESTIONS[currentQuestion]}</p>
                <div>
                  <button onClick={() => this.handleAnswer("Yes")}>Yes</button>
                  <button onClick={() => this.handleAnswer("No")}>No</button>
                </div>
                <button onClick={this.handleNextQuestion}>Next</button>
              </div>
            ) : (
              <div>
                <h1>Quiz completed!</h1>
                <p>Average Score: {averageScore}</p>
                <button onClick={() => window.location.reload()}>Restart</button>
              </div>
            )}
          </div>
        </main>
      </div>
    );
  }
}

export default App;
